import React, { useState, useRef } from "react";
import RichTextEditor from "./Components/RichTextEditor";
import Button from '@material-ui/core/Button'
import "./App.css";
import parse from 'html-react-parser';
import Menu from '@mui/material/Menu';
import MenuItem from '@mui/material/MenuItem';
import PopupState, { bindTrigger, bindMenu } from 'material-ui-popup-state';
import List from '@mui/material/List';
import ListItem from '@mui/material/ListItem';
import ListItemText from '@mui/material/ListItemText';
var aws = require('aws-sdk/dist/aws-sdk-react-native');


//import EditIcon from '@mui/icons-material/Edit';
const mapObj = {"S3":"<a href='https://aws.amazon.com/s3/' >Simple Storage Service (S3)</a>",
Gateway:'<a href="https://aws.amazon.com/api-gateway/" >API Gateway</a>',
Lambda: '<a href="https://aws.amazon.com/lambda/" >Lambda</a>',
MSK: '<a href="https://aws.amazon.com/msk/" >Amazon Managed Streaming for Apache Kafka (MSK)</a>'
};

const options = [
  'Language',
  'Spanish',
  'Italian'
];


//Amazon configuration parameters
aws.config = new aws.Config();
aws.config.accessKeyId = "AKIA6MXJ3KNL6HRRPLH4";
aws.config.secretAccessKey = "OHueVaHKxFVR6G6Vtrr8s+u/a64m2fQm+J7oxcFw";
aws.config.region = "eu-west-1";
let finaldata = "";

function replaceAll(str,mapObj){
  var re = new RegExp(Object.keys(mapObj).join("|"),"gi");
  return str.replace(re, function(matched){
      return mapObj[matched];
  });
}

const App = () => {
  const [anchorEl, setAnchorEl] = useState("");
  const [selectedIndex, setSelectedIndex] = useState("");
  const open = Boolean(anchorEl);
  const [language, setLanguage] = useState("");
  const handleClickListItem = (event: React.MouseEvent<HTMLElement>) => {
  setAnchorEl(event.currentTarget);
};
  const [newvalue, setnewvalue] = useState('');


const handleMenuItemClick = (
  event: React.MouseEvent<HTMLElement>,
  index: number,
) => {
  setSelectedIndex(index);
  setAnchorEl(null);
  console.log("El valor es " + selectedIndex);
  if (index == 1) {
    setLanguage("es");
    setnewvalue("");
      console.log("Target Language is: Spanish: " + language);
  } else {
    setLanguage("it");
    setnewvalue("");

      console.log("Target Language is: Italian: " + language);
  }
};

const handleClose = () => {
  setAnchorEl(null);
};


  const [value, setValue] = useState("");
  const [message, setMessage] = useState("");
  const [messageUpdated, setMessageUpdated] = useState("");
  const [messageTranslated, setMessageTranslated] = useState("");
  const [messageUpdatedTranslated, setMessageUpdatedTranslated] = useState('');
  const [translatedfinaldata, settranslatedfinalData] = useState('');



  //This function is called when something in the first text box changes
  const getValue = (value) => {
    setValue(value);
    var data = value.replace(/<[^>]+>/g, '');
    setMessage(data);
    setValue(data);
    let mod = replaceAll(value, mapObj);
    setMessageUpdated(mod);
  }
  //This handles the logic of the Translate BUTTON
  const handleSubmit = (e) => {

    const translate = new aws.Translate({ region: 'eu-west-1' });
    const SourceLanguageCode = "en";
    const TargetLanguageCode = language;
    const Text = value;

    //e.preventDefault()

          const params = {
              SourceLanguageCode, TargetLanguageCode, Text
          }

          try {
              translate.translateText(params, (err, data) => {
                  if (err) {
                      if (err.code == 'MissingRequiredParameter') {
                          console.log("ERROR 1: " + err);
                      }
                      if (err.code == 'MultipleValidationErrors') {
                          console.log("ERROR 2: " + err );
                      }
                          console.log("ERROR 3 " + err );
                  } else {
                  finaldata = data;

                  setMessageTranslated(finaldata.TranslatedText);
                  const dataNew = finaldata.TranslatedText;
                  let mod = replaceAll(dataNew, mapObj);
                  let modificado = "\"" + mod + "\"";
                  setMessageUpdatedTranslated(mod);
                  console.log("El valor enviado  es " + selectedIndex + " " + language);
                  console.log("El textro traducido es: " + finaldata.TranslatedText);


                }
              });
          } catch (error) {
              console.log("ERROR HERE: " + error);
          }
  }


  //It returns Two text box and one button
  return (
    <div className="row">
      <div className="col-md-6" style={{ margin: "auto", marginTop: "50px" }}>
        <div style={{ textAlign: "center" }}>
          <h3>Email Editor </h3>
        </div>
        <p> <RichTextEditor initialValue={messageTranslated} getValue={getValue} /></p>
        <p> <RichTextEditor initialValue={messageUpdatedTranslated} getValue={getValue} /></p>
        <p><form noValidate autoComplete='off'>
        <div class="buttons">
          <div class="action_btn">
            <Button variant="contained" color="primary" onClick={handleSubmit}>Translate</Button>
            <List
        component="nav"
        aria-label="Device settings"
        sx={{ bgcolor: 'background.paper' }}
      >
        <ListItem
          button
          id="lock-button"
          aria-haspopup="listbox"
          aria-controls="lock-menu"
          aria-label="Language"
          aria-expanded={open ? 'true' : undefined}
          onClick={handleClickListItem}
        >
          <ListItemText
            primary="Choose a Language"
            secondary={options[selectedIndex]}
          />
        </ListItem>
      </List>
      <Menu
        id="lock-menu"
        anchorEl={anchorEl}
        open={open}
        onClose={handleClose}
        MenuListProps={{
          'aria-labelledby': 'lock-button',
          role: 'listbox',
        }}
      >
        {options.map((option, index) => (
          <MenuItem
            key={option}
            disabled={index === 0}
            selected={index === selectedIndex}
            onClick={(event) => handleMenuItemClick(event, index)}
          >
            {option}
          </MenuItem>
        ))}
      </Menu>
          </div>
         </div>
          </form>
        </p>
      </div>
    </div>
  );
};

export default App;
